#include <iostream>
#include <vector>
#include <algorithm>
#include <string>

using namespace std;

class Solution 
{
public:
	int firstUniqChar(string s) 
	{
		vector<int> map(26);
		int result = -1;


		for_each(s.begin(), s.end(), [&map](char element) {
			map[element - 97]++;
		});


		int index = 0;
		find_if(s.begin(), s.end(), [&index, &result, &map](char element) {
			if (map[element - 97] == 1)
			{
				result = index;
				return true;
			}

			index++;
			return false;
		});

		return result;
	}
};

int main()
{
	Solution s;

	int location = s.firstUniqChar("");

	getchar();

	return 0;
}